/** @format */
import React from "react";
import "./App.css";
// import fetchMovie from "./components/api";
import Loading from "./components/loading";
import MovieList from "./movie-info";

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      activeMovie: "avengers",
      loading: true,
      allMovies: []
    };

    this.getData = this.getData.bind(this);
  }

  componentDidMount() {
    var APIKEY = "b940697ef71b1bf05d8c3d3282d547bf";
    var API2 =
      "https://api.themoviedb.org/3/search/movie?api_key=b940697ef71b1bf05d8c3d3282d547bf&language=en-US&page=1&include_adult=false&query=avengers";
    var API = `https://api.themoviedb.org/3/search/movie?api_key=${APIKEY}&language=en-US&page=1&query=${movie}`;

    return new Promise((resolve, reject) => {
      fetch(API2)
        .then(data => data.json())
        .then(movies => console.log(movies) || movies.items)
        .catch(error => {
          reject(error);
        });
    });
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevState.activeMovie !== this.state.activeMovie) {
      this.getData(this.state.activeMovie);
    }
  }

  // getData(movie) {
  //   this.setState({
  //     loading: true
  //   });

  //   this.fetchMovie(movie).then(data => {
  //     this.setState({
  //       allMovies: data,
  //       loading: false
  //     });
  //   });
  // }

  handleChangeMovie(newMovie) {
    this.setState({
      activeMovie: newMovie
    });
  }

  render() {
    console.log(this.state);
    if (this.state.loading === true) {
      return <Loading />;
    }
    return (
      <div>
        <MovieList movies={this.state.allMovies} />
      </div>
    );
  }
}

export default App;
