/** @format */
import React from "react";
import "./App.css";
import fetchMovie from "./components/main-movie";
import Loading from "./components/loading";
import MovieList from "./components/movie-list";
import Navbar from "./components/movie-search";
import GithubLogo from "./components/github";
import fetchSimilarMovies from "./components/similar-movie-api";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      activeMovie: "avengers",
      loading: true,
      mainMovie: [],
      id: [],
      similarMovies: []
    };

    this.getData = this.getData.bind(this);
    this.handleChangeMovie = this.handleChangeMovie.bind(this);
  }

  componentDidMount() {
    this.getData(this.state.activeMovie);
  }

  async getData(movie) {
    this.setState({
      loading: true
    });

    await fetchMovie(movie).then(data => {
      this.setState({
        mainMovie: data,
        loading: true,
        id: data[0].id
      });
    });

    await fetchSimilarMovies(this.state.id).then(data => {
      this.setState({
        similarMovies: data,
        loading: false
      });
    });
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevState.activeMovie !== this.state.activeMovie) {
      this.getData(this.state.activeMovie);
    }
  }

  handleChangeMovie(newMovie) {
    this.setState({
      activeMovie: newMovie
    });
  }

  render() {
    if (this.state.loading === true) {
      return <Loading />;
    }
    return (
      <Router>
        <div>
          <div id="navbar">
            <ul>
              <li>
                <Link to="/search">Movie Search</Link>
              </li>
              <li>
                <Link to="/popular">Popular Movies</Link>
              </li>
              <li>
                <Link to="/filter">Movie Filter</Link>
              </li>
            </ul>
          </div>

          <Route exact path="/search" component={GithubLogo}></Route>
          <GithubLogo />
          <Navbar
            activeMovie={this.state.activeMovie}
            changeMovie={this.handleChangeMovie}
          />
          <MovieList
            id={this.state.id}
            movie={this.state.mainMovie[0]}
            movies={this.state.similarMovies}
          />
        </div>
      </Router>
    );
  }
}

export default App;
