/** @format */

import React from "react";
import "./App.css";
import fetchMovie from "./components/api";

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      activeMovie: "Avengers",
      loading: undefined,
      allMovies: []
    };
  }

  componentDidMount() {
    this.getData(this.activeMovie);
    console.log(this.activeMovie);
  }

  getData(movie) {
    this.setState({
      loading: true
    });

    fetchMovie(movie).then(data =>
      this.setState({
        allMovies: data,
        loading: false
      })
    );
  }

  render() {
    return <div></div>;
  }
}

export default App;
